from data_parser import queryfrom flask import Flask, jsonify, requestapp = Flask(__name__)@app.route("/restaurants/search")def search():    q = request.args.get('q')    if len(q) == 0:        return "A minimum length for the query string is one character."    if q.isupper() is True:        q = q.lower()    lat = request.args.get('lat')    lon = request.args.get('lon')    ret = query(q, lat, lon)    if not ret:        return "No results found with search parameters"    else:        return jsonify(ret), 200@app.route("/", methods=['GET'])def test():    return jsonify({'instructions': 'https://github.com/MikeyLHolm/Wolt2020_REST_API'})if __name__ == "__main__":    app.run(debug=True, port=5000)