# If flask stuck running:# $ ps -fA | grep python# 501 81651 12648   0  9:53PM ttys000    0:00.16 python -m SimpleHTTPServer# kill 81651from data_parser import queryimport osfrom flask import Flask, jsonify, requestapp = Flask(__name__)@app.route("/restaurants/search")def search():    q = request.args.get('q')    if q.islower() is False:        return "Please use lowercase letters for your query."    lat = request.args.get('lat')    lon = request.args.get('lon')    try:        os.remove('results.json')    except OSError:        pass    ret = query(q, lat, lon)    return jsonify(ret)@app.route("/restaurants", methods=['GET'])def test():    return jsonify({'helpful message': 'Woah it kinda works!'})if __name__ == "__main__":    app.run(debug=True, port=5000)